## 
public  index.php 
~~~
<?php

// Autoload 
require '../vendor/autoload.php';

// 
require '../config/routes.php';
~~~
 Composer 
configconfig routs.php 
~~~
<?php

use NoahBuscher\Macaw\Macaw;

Macaw::get('fuck', function() {
  echo "";
});

Macaw::get('(:all)', function($fu) {
  echo '<br>'.$fu;
});

Macaw::dispatch();
~~~

